// Make an object pointing to the location of the Flash movie on your web server.
// Try using the font name as the variable name, makes it easy to remember which
// object you're using. As an example in this file, we'll use Futura.
// var archerbold = { src: '/flash/archer-bold.swf' };

// Now you can set some configuration settings.
// See also <http://wiki.novemberborn.net/sifr3/JavaScript+Configuration>.
// One setting you probably want to use is `sIFR.useStyleCheck`. Before you do that,
// read <http://wiki.novemberborn.net/sifr3/DetectingCSSLoad>.

// sIFR.useStyleCheck = true;

// Next, activate sIFR:
// sIFR.activate(archerbold);

// If you want, you can use multiple movies, like so:
//
//    var futura = { src: '/path/to/futura.swf' };
//    var garamond = { src '/path/to/garamond.swf' };
//    var rockwell = { src: '/path/to/rockwell.swf' };
//    
//    sIFR.activate(futura, garamond, rockwell);
//
// Remember, there must be *only one* `sIFR.activate()`!

// Now we can do the replacements. You can do as many as you like, but just
// as an example, we'll replace all `<h1>` elements with the Futura movie.
// 
// The first argument to `sIFR.replace` is the `futura` object we created earlier.
// The second argument is another object, on which you can specify a number of
// parameters or "keyword arguemnts". For the full list, see "Keyword arguments"
// under `replace(kwargs, mergeKwargs)` at 
// <http://wiki.novemberborn.net/sifr3/JavaScript+Methods>.
// 
// The first argument you see here is `selector`, which is a normal CSS selector.
// That means you can also do things like '#content h1' or 'h1.title'.
//
// The second argument determines what the Flash text looks like. The main text
// is styled via the `.sIFR-root` class. Here we've specified `background-color`
// of the entire Flash movie to be a light grey, and the `color` of the text to
// be red. Read more about styling at <http://wiki.novemberborn.net/sifr3/Styling>.

// No more stats at the top
// sIFR.replace(archerbold, {
//   selector: '#stats span',
//   css: '.sIFR-root { color: #224470; white-space:nowrap}',
//   wmode: 'transparent',
//   forceSingleLine: true
// 
// });

function getInternetExplorerVersion()
// Returns the version of Internet Explorer or a -1.
// -1 indicates the use of another browser.
{
  var rv = -1; // Return value assumes failure.
  if (navigator.appName == 'Microsoft Internet Explorer')
  {
    var ua = navigator.userAgent;
    var re  = new RegExp("MSIE ([0-9]{1,}[\.0-9]{0,})");
    if (re.exec(ua) != null)
      rv = parseFloat( RegExp.$1 );
  }
  return rv;
}

var ie_version = getInternetExplorerVersion();

if(-1 == ie_version || ie_version >= 8.0) {
  
  var archerbold = { src: '/flash/archer-bold.swf' };
   
  sIFR.activate(archerbold);
   
  // .slideshow .pic .copy p,   , .slideshow .copy h6.active_feature_title
  sIFR.replace(archerbold, {
    selector: '#player .copy p, #player .copy h6.active_feature_title, .slideshow .copy h6.active_project_name',
    css: '.sIFR-root { color: #ffffff}',
    wmode: 'transparent',
    forceSingleLine: true
  });
   
  sIFR.replace(archerbold, {
    selector: 'h1.home',
    css: ['.sIFR-root { color: #224470; text-align:center}', 'a {color:#224470; text-decoration:none}', 'a:hover {color:#224470;text-decoration:underline}'],
    wmode: 'transparent',
    forceSingleLine: false
   
  });
   
  sIFR.replace(archerbold, {
    selector: '#player h4, h1.larger, h1.inline, #fundraising h1',
    css: ['.sIFR-root { color: #224470}', 'a {color:#224470; text-decoration:none}', 'a:hover {color:#224470;text-decoration:underline}'],
    wmode: 'transparent',
    forceSingleLine: false
   
  });
   
  sIFR.replace(archerbold, {
    selector: '.box-rightcol-inner h4, .profile ul li.contact .follow, .box-full-middle h4, .component h4 ',
    css: ['.sIFR-root { color: #224470; letter-spacing: 1; font-weight:bold}', 'a {color:#224470; text-decoration:none}', 'a:hover {color:#224470;text-decoration:underline}'],
    wmode: 'transparent',
    forceSingleLine: true
   
  });
   
  sIFR.replace(archerbold, {
    selector: 'h1.search',
    css: '.sIFR-root { color: #224470; letter-spacing: 0.02; font-weight:bold}',
    wmode: 'transparent',
    forceSingleLine: false
   
  });

}


